INSERT INTO `products` (`product_id`, `product_cat1`, `product_cat2`, `product_cat3`, `product_cat4`, `product_title`, `product_price`, `product_diy_price`, `product_desc`, `product_keywords`, `product_image`, `product_doc`, `product_block`, `product_hs`, `product_high`, `product_where`) VALUES
(20312, 'Android Projects', 'Sensor Based Projects', 'Arduino/AVR Projects', 'Bluetooth Based Projects', 'Bluetooth Control Vehicle (Android app control)', 3602, 3215, '<p style="text-align: justify;">Bluetooth controlled car is controlled by using Android mobile phone instead of any other method like buttons, gesture etc. Here only needs to touch button in android phone to control the car in forward, backward, left and right directions. So here android phone is used as transmitting device and Bluetooth module placed in car is used as receiver. Android phone will transmit command using its in-built Bluetooth to car so that it can move in the required direction like moving forward, reverse, turning left, turning right and stop.</p>\r\n<p style="text-align: justify;">The working of this project depends upon the connection between two Bluetooth modules. On the android mobile side, the Bluetooth module is the inbuilt module of android phone and on the vehicle side it is the HC-05 Bluetooth module. The vehicle has two DC motors used to control the movement and direction of the vehicle. Bluetooth module is used to receive command from android phone and Arduino UNO is used for controlling the whole system.</p>', 'bluetooth, arduino control ', 'Maker_Club-controlled-car.jpg.jpg', 'Elecbits_Bluetooth Control vehicle_synopsis.doc', 'a_bluetooth control vehile.png', '<p><span style="text-decoration: underline;"><strong>HARDWARE:</strong></span></p>\r\n<ul>\r\n<li>General Purpose Board</li>\r\n</ul>\r\n<ul>\r\n<li>Single Strand Wires</li>\r\n</ul>\r\n<ul>\r\n<li>DC Power Supply (Using 9V Battery)</li>\r\n</ul>\r\n<ul>\r\n<li>Darlington Pair TIP122</li>\r\n</ul>\r\n<ul>\r\n<li>Ceramic Capacitors</li>\r\n</ul>\r\n<ul>\r\n<li>Resistors</li>\r\n</ul>\r\n<ul>\r\n<li>Potentiometer</li>\r\n</ul>\r\n<ul>\r\n<li>Diodes</li>\r\n</ul>\r\n<ul>\r\n<li>DC 150 RPM Motors</li>\r\n</ul>\r\n<ul>\r\n<li>Arduino UNO</li>\r\n</ul>\r\n<ul>\r\n<li>HC-05 Bluetooth Module</li>\r\n</ul>\r\n<ul>\r\n<li>LCD 16*2</li>\r\n</ul>\r\n<p><span style="text-decoration: underline;"><strong>SOFTWARE:</strong></span></p>\r\n<ul>\r\n<li>Arduino Software</li>\r\n</ul>\r\n<ul>\r\n<li>Bluetooth Android application</li>\r\n</ul>', '<ul>\r\n<li>Microcontroller Based</li>\r\n</ul>\r\n<ul>\r\n<li>Uses Android Communication</li>\r\n</ul>\r\n<ul>\r\n<li>Uses HC-05 Modules For Fast Data Transmission</li>\r\n</ul>\r\n<ul>\r\n<li>Self-Explanatory Kit Available</li>\r\n</ul>\r\n<ul>\r\n<li>How-to-make Video Available</li>\r\n</ul>\r\n<ul>\r\n<li>Synopsis Available</li>\r\n</ul>\r\n<ul>\r\n<li>Complete File Available</li>\r\n</ul>', 'General'),
(120622, 'Sensor Based Projects', 'IR and Ultrasonic Projects', 'Arduino/AVR Projects', 'Simple Electronics Projects', 'Line Following vehicle (Using Microcontroller)', 3575, 3200, '<p style="text-align: justify;">This robot is a mobile device that detects and follows the line drawn on the floor. The path must be in a visible black line on a white surface. A line following robot is a mobile machine employed to sense and follow the black lines that are drawn on the white surface. This technique can be incorporated into the Automated Guided Vehicles (AGV) for providing the easy way of operation.</p>\r\n<p style="text-align: justify;">The line following vehicle works by identifying the black line over a white line by using different sensors. The most common sensors used are photo resistors or infrared transmitter and receiver pair. The sensor sense the black line using the light reflection mechanism. When white light falls on a black surface it gets absorbed and less light is reflected as compared to the white line. The sensor sense this change in the intensity of the reflected lighted and thus instruct the microcontroller and the microcontroller further controls the motor movements accordingly. When a photo resistor is used as the sensor the resistance of the photo resistor change in accordance with the intensity of the reflected light. Generally 2 photo resistors are enough to develop a fully functional line following robot but 4 or more photo resistors are used in order to get better precision.&nbsp;</p>', 'line, vehicle, line following ', '94919-01.jpg', 'Elecbits_lfr_synopsis.doc', 'bd.png', '<p><span style="text-decoration: underline;"><strong>HARDWARE:</strong></span></p>\r\n<ul>\r\n<li>General purpose Board</li>\r\n</ul>\r\n<ul>\r\n<li>Single Strand Wires</li>\r\n</ul>\r\n<ul>\r\n<li>DC Power Supply (Using 9V Battery)</li>\r\n</ul>\r\n<ul>\r\n<li>Darlington Pair TIP122</li>\r\n</ul>\r\n<ul>\r\n<li>Ceramic Capacitors</li>\r\n</ul>\r\n<ul>\r\n<li>Resistors</li>\r\n</ul>\r\n<ul>\r\n<li>Potentiometer</li>\r\n</ul>\r\n<ul>\r\n<li>Diodes</li>\r\n</ul>\r\n<ul>\r\n<li>DC 150 RPM Motors</li>\r\n</ul>\r\n<ul>\r\n<li>Arduino UNO</li>\r\n</ul>\r\n<ul>\r\n<li>Line Detecting Sensor Array&nbsp;</li>\r\n</ul>\r\n<ul>\r\n<li>LCD 16*2</li>\r\n</ul>\r\n<p><span style="text-decoration: underline;"><strong>SOFTWARE:</strong></span></p>\r\n<ul>\r\n<li>Arduino Software</li>\r\n</ul>', '<ul>\r\n<li>Microcontroller Based</li>\r\n</ul>\r\n<ul>\r\n<li>Uses Sensor Array For Better Results</li>\r\n</ul>\r\n<ul>\r\n<li>Self Explanatory Kit Available</li>\r\n</ul>\r\n<ul>\r\n<li>How-to-make Videos Available</li>\r\n</ul>\r\n<ul>\r\n<li>Synopsis Available</li>\r\n</ul>\r\n<ul>\r\n<li>Complete File Available</li>\r\n</ul>', 'General'),
(131910, 'Radio frequency Projects', 'Without Micro-controller Projects', 'Simple Electronics Projects', 'Communication Projects', 'Mobile Signal Jammer (A simple circuitry)', 2649, 2219, '<p style="text-align: justify;">A mobile phone jammer is an instrument used to prevent cellular phones from receiving signals from base stations. When used, the jammer effectively disables cellular phones. These devices can be used in practically any location, but are found primarily in places where a phone call would be particularly disruptive because silence is expected. The Mobile jammer works on the principle of radio jammer. A radio jammer is any device that deliberately blocks, jams or interferes with authorized wireless communications&nbsp;</p>\r\n<p style="text-align: justify;">Jamming devices overpower the cell phone by transmitting a signal on the same frequency and at a high enough power that the two signals collide and cancel each other out. Cell phones are designed to add power if they experience low-level interference, so the jammer must recognize and match the power increase from the phone. Cell phones are full-duplex devices, which means they use two separate frequencies, one for talking and one for listening simultaneously. Some jammers block only one of the frequencies used by cell phones, which has the effect of blocking both. The phone is tricked into thinking there is no service because it can receive only one of the frequencies.</p>', 'jammer, signal jammer, mobile', 'mobil-jammer.jpg', 'Elecbits_Mobile Signal Jammer_synopsis.doc', 'a_mobile signal jammer.jpg', '<p><span style="text-decoration: underline;"><strong>HARDWARE:</strong></span></p>\r\n<ul>\r\n<li>General Purpose Board</li>\r\n</ul>\r\n<ul>\r\n<li>Ceramic Capacitors</li>\r\n</ul>\r\n<ul>\r\n<li>Electrolytic Capacitors</li>\r\n</ul>\r\n<ul>\r\n<li>Tantalum Capacitors</li>\r\n</ul>\r\n<ul>\r\n<li>Resistors</li>\r\n</ul>\r\n<ul>\r\n<li>Connecting wires</li>\r\n</ul>\r\n<ul>\r\n<li>Crystal Oscillator</li>\r\n</ul>\r\n<ul>\r\n<li>DC Power Supply</li>\r\n</ul>\r\n<ul>\r\n<li>Antenna<br /><br /></li>\r\n</ul>\r\n<p><span style="text-decoration: underline;"><strong>SOFTWARE:</strong></span></p>\r\n<ul>\r\n<li>NA</li>\r\n</ul>', '<ul>\r\n<li>No Microcontroller Required</li>\r\n</ul>\r\n<ul>\r\n<li>No Coding required</li>\r\n</ul>\r\n<ul>\r\n<li>Variable Frequency Selection</li>\r\n</ul>\r\n<ul>\r\n<li>Self-Explanatory Kit Available</li>\r\n</ul>\r\n<ul>\r\n<li>How-to-make Videos Available</li>\r\n</ul>\r\n<ul>\r\n<li>Synopsis Available</li>\r\n</ul>\r\n<ul>\r\n<li>Complete File Available</li>\r\n</ul>', 'General'),
(731822, 'Artificial Intelligence Projects', 'Gesture Control Projects', 'Communication Projects', 'Bluetooth Based Projects', 'Gesture Control Robotic Vehicle', 5588, 5030, '<p style="text-align: justify;">A gesture controlled robot using an accelerometer is one kind of robot which can be operated by the movement of hand by placing an accelerometer on it. This project is divided into two parts transmitter device and receiver device. Where a gesture device works as a transmitter device and a robot works as a receiver device. When a transmitting device (accelerometer) is placed on the hand, then it will send signals to the robot for the required operation.</p>\r\n<p style="text-align: justify;">In this project the robotic vehicle is controlled by using different gestures of human hand. The robotic vehicle movement in the respective directions that is left, right, front and backwards is controlled in accordance with tilting the hand in the respective directions.This project is divided into two parts transmitter device and receiver device. Where a gesture device works as a transmitter device and a robot works as a receiver device. When a transmitting device (accelerometer) is placed on the hand, then it will send signals to the robot for the required operation.</p>', 'gesture, robotic vehicle, mimic ', 'gesture-control.jpg', 'Elecbits_Gesture Control robotic vehile_synopsis.doc', 'a_gesture control robotic vechile.png', '<p><span style="text-decoration: underline;"><strong>HARDWARE:</strong></span></p>\r\n<ul>\r\n<li>DC Motors</li>\r\n</ul>\r\n<ul>\r\n<li>Capacitors</li>\r\n</ul>\r\n<ul>\r\n<li>Resistors</li>\r\n</ul>\r\n<ul>\r\n<li>Diodes</li>\r\n</ul>\r\n<ul>\r\n<li>Connecting Wires</li>\r\n</ul>\r\n<ul>\r\n<li>Jumper wires</li>\r\n</ul>\r\n<ul>\r\n<li>General Purpose Board</li>\r\n</ul>\r\n<ul>\r\n<li>Darlington Pair TIP122</li>\r\n</ul>\r\n<ul>\r\n<li>HC-05 Bluetooth Module</li>\r\n</ul>\r\n<ul>\r\n<li>Accelerometer sensor ADXL335</li>\r\n</ul>\r\n<p><span style="text-decoration: underline;"><strong>SOFTWARE:</strong></span></p>\r\n<ul>\r\n<li>Microcontroller HEX File Uploader</li>\r\n</ul>', '<ul>\r\n<li>Uses Bluetooth Modules For Communication</li>\r\n</ul>\r\n<ul>\r\n<li>Four Controls Of Freedom</li>\r\n</ul>\r\n<ul>\r\n<li>Respond according to the tilt Of The Hand</li>\r\n</ul>\r\n<ul>\r\n<li>Self-Explanatory Kit Available</li>\r\n</ul>\r\n<ul>\r\n<li>Self-Explanatory Kit Available</li>\r\n</ul>\r\n<ul>\r\n<li>Synopsis Available</li>\r\n</ul>\r\n<ul>\r\n<li>Complete File Available</li>\r\n</ul>', 'Trending_now'),
(2115422, 'Sensor Based Projects', 'IR and Ultrasonic Projects', 'Arduino/AVR Projects', 'Arduino/AVR Projects', 'Ultrasonic Based Obstacle Detecting Vehicle', 3740, 3340, '<p style="text-align: justify;">A collision avoidance system reduce the severity of an accident. Also known as pre-crash system, forward collision warning system or collision mitigating system, it uses radar and sometimes laser and camera (both sensor types are ineffective during bad weather) to detect an imminent crash. Once the detection is done, these systems either provide a warning to the driver when there is an imminent collision or take action autonomously without any driver input (by braking or steering or both). A collision avoidance system reduce the severity of an accident. Also known as pre-crash system, forward collision warning system or collision mitigating system, it uses radar and sometimes laser and camera (both sensor types are ineffective during bad weather) to detect an imminent crash. Once the detection is done, these systems either provide a warning to the driver when there is an imminent collision or take action autonomously without any driver input (by braking or steering or both).&nbsp;</p>\r\n<p style="text-align: justify;">Whenever the robot is going on the desired path the ultrasonic sensor transmits the ultrasonic waves continuously from its sensor head. Whenever an obstacle comes ahead of it the ultrasonic waves are reflected back from an object and that information is passed to the microcontroller. The microcontroller controls the motors left, right, back, front, based on ultrasonic signals. In order to control the speed of each motor pulse width modulation is used (PWM).</p>', 'ultrasonic, obstacle detctor', 'Ultrasonic Obstacle Detecting Robot_logo.jpg', 'Elecbits_Ultrasonic Obstacle Detecting Vehicle_synopsis.doc', 'Ultrasonic Obstacle Detecting Robot_bd.png', '<p><span style="text-decoration: underline;"><strong>HARDWARE:</strong></span></p>\r\n<ul>\r\n<li>Gene